import React from "react";

import github from "../assets/images/github.png";

//  put new image links here:

// import github from "../assets/github";
// import github from "../assets/github";

class Carousel extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [
        {
          id: 0,
          title: "",
          subTitle: "",
          imgSrc: "github",
          link: "",
          selected: false,
        },
        {
          id: 1,
          title: "",
          subTitle: "",
          imgSrc: "github",
          link: "",
          selected: false,
        },
        {
          id: 2,
          title: "",
          subTitle: "",
          imgSrc: "github",
          link: "",
          selected: false,
        },
      ],
    };
  }

  handleCardClick = (id, card) => {
    let items = [...this.state.items];

    items[id].selected = items[id].selected ? false : true;

    items.forEach((item) => {
      if (item.id !== id) {
        item.selected = false;
      }
    });

    this.setState({
      items,
    });
  };

  render() {
    return <p>Carousel Works!</p>;
  }
}

export default Carousel;
